(window.webpackJsonp=window.webpackJsonp||[]).push([[52],{449:function(v,_,p){"use strict";p.r(_);var a=p(25),e=Object(a.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("p",[v._v("名士风流\n公开 2013-05-02 20:25 | (分类:默认分类)")]),v._v(" "),p("p",[v._v("《睿一八部》之六")]),v._v(" "),p("p",[v._v("我的朋友王睿一是一个宅男，我们组织自驾去青城山泡温泉，他都不乐意去，要知道青城山离成都市区的距离还没他两瓣屁股的距离远。我告诉他，这次自驾去泡温泉，你不用驾，只管泡就行了。他说，我懒得泡。")]),v._v(" "),p("p",[v._v("连泡温泉都嫌麻烦，由此可知他对泡妞的懈怠，并不是因为他有什么生理缺陷，仅仅是因为他懒得泡而已。")]),v._v(" "),p("p",[v._v("可是这次他竟然三番五次地主动邀我共赴深圳旅游，提前两周就订好了机票，那兴奋劲，就像小学生在准备春游的食物，这让我百思不得其解。我问他，你为什么对深圳这么感兴趣？")]),v._v(" "),p("p",[v._v("他说有一种冥冥的力量将他拉向这座城市，万夫莫当。他把这种来自深圳的冥冥力量称作“大有深意”。我问他这深意有多深，他说像女人的心一样深。")]),v._v(" "),p("p",[v._v("我知道王睿一长这么大从来没有参透过女人的心，其实我又何尝不是如此。我拍拍他的肩，安慰他道：深圳是一个创造奇迹的地方，你要相信自己，there will be miracle, when you believe. 当时我们正在深圳宝安机场上空盘旋，很快即将降落，王睿一用手指着窗外东莞的方向，就像改革开放总设计师那样画了一个圈，答非所问地说，你说刘作爱会不会带我们去玩些比较特别的项目，要是那样，我可不去！")]),v._v(" "),p("p",[v._v("我在深圳的朋友刘作爱在机场接到了我俩，我和她热情拥抱，感觉她胸大如昔。王睿一跟在我俩屁股后面一言不发，刘作爱问我他在想啥，我说他在思考旅行的意义。刘作爱回头告诉王睿一，你不是在思考旅行的意义吗？我来告诉你。王睿一听了后有些兴奋，一路小跑紧跟了上来，只见刘作爱掏出手机，在电话里传达指示，像在点菜，又像在安排公关。她说：“多来点花甲。”王睿一小声问我，花甲是什么？我想了想回答他，就是60岁的人。王睿一勃然大怒，来回摇晃着刘作爱稚嫩的肩膀，说能不能来点而立，我喜欢少妇！")]),v._v(" "),p("p",[v._v("刘作爱委屈地说花甲是一种贝壳，就是北方说的蛤蜊。王睿一失望之情溢于言表，他说不就是gala么，还叫得这么文艺，他说广东的gala没北方的好，炒熟过后肉里全是腥臭的液体，被称作骚gala. 刘作爱好学地请教王睿一，为什么北方的gala就要清纯许多？王睿一比划着回答她，我们北方的gala都是年轻gala，年方二十，不像你们这里的老gala，当然清纯啦！")]),v._v(" "),p("p",[v._v("我觉得王睿一对深圳有些由爱生恨，赶紧拉住了他的胳膊，示意他多看看这里好的一面，比如刘作爱即将带我们去的那家烧烤店，叫做木屋烧烤，据说是奥巴马的弟弟开的，在整个广东无人不晓，那里的海鲜烧烤可好吃啦，你在别的地方一辈子都吃不到。我到了木屋烧烤才知道，王睿一对花甲属于明贬实褒，他在一个小时内至少吃了三盘烤花甲，任由那60岁的腥臭液体顺着嘴角流下。而我则不和他一般见识，点了一道炒石螺，因为我听说广东省副省长每次来这里都会吃这道石螺。我学着省级干部的样子，稳重地吞下一只石螺肉，觉得有点臭，比起花甲来，算是闷骚。结果刘作爱在一旁笑得前仰后合，她说石螺肉只能吃上半身，你把下半身也吃了，丫内脏都在里面，还有屎呢！你这个北方来的土逼。")]),v._v(" "),p("p",[v._v("一旁的服务员也震惊了，他们一传十十传百，用了不到十分钟，整个木屋烧烤都知道有个成都人吃了石螺的屎。我听见一名服务员用广东话说，奥巴马弟弟的那帮肯尼亚亲戚连字都不认识，签字都只能按手印，但人家也没吃过屎。另一名服务员回忆道，这批石螺饮食不规律，这几天正在便秘，我大概连它青春期时形成的屎都一并吃下去了。")]),v._v(" "),p("p",[v._v("我气得把一个啤酒瓶攥在手里，随时准备发作。我甚至还给自己设计了一段粤语台词，但又怕不够地道，于是就此请教刘作爱：我可不可以问那个服务员，你知唔知我系边位啊？")]),v._v(" "),p("p",[v._v("刘作爱说这里的服务员会回答你：我管你系边位啊！")]),v._v(" "),p("p",[v._v("广东人果然见过大世面！我回忆着香港黑帮电影中大排档里的腥风血雨，默默地把酒瓶放回了桌下，心中别提多憋屈了。")]),v._v(" "),p("p",[v._v("这时候王睿一路见不平。他最近正在研究晋朝的清谈之风，擅长辩难，于是学以致用地问刘作爱，你知不知道我们的世界有几个维度？")]),v._v(" "),p("p",[v._v("刘作爱答道，长宽高。王睿一又问她，假设二维世界里也存在生命，那么对这些生命来说，他们就没有高的概念。如何能让他们感受到三维世界的物体呢？他们是不能用肉眼直接观察三维物体的，因为他们只能看到两个维度。所以我们可以让一个三维物体穿过一个平面，当二者发生接触时，两个世界就相交了，三维物体会被二维平面截断，这个截面就是二维生命能够观察到的三维物体的信息，想象一下，一个肥皂泡掉落在桌上的情形。")]),v._v(" "),p("p",[v._v("刘作爱天赋极高，她替王睿一补充道，如果这个肥皂泡不破掉，那么它和桌子接触的截面就会随着肥皂泡和桌面的相对位置而变化，接触得越多，截面面积越大。由于肥皂泡是规则的球体，所以那个截面永远都是圆形，但如果三维物体是不规则的，那么和二维平面相交形成的截面就是不规则的平面图形，这个图形随着三维物体穿过二维平面而不停地变化，直至三维物体完全穿过平面时，截面消失，平面世界里的二维生命就再也看不见那个三维物体了。")]),v._v(" "),p("p",[v._v("刘作爱甚至举一反三地推论道，她一直觉得自己男友长得像被打扁了的余文乐，看来她男友其实是余文乐在二维世界的投影，只是余文乐穿越二维世界时卡住不动了，所以她男友还一直存在。讲到这里，刘作爱意识到自己男友是个二维生物，觉得有些抬不起头来。")]),v._v(" "),p("p",[v._v("王睿一竖起大拇指，夸刘作爱胸大又有脑，实属罕见。他继续分析，说三维物体穿过平面世界时，在二维生命眼里，他们只能看到一个不停变化形状和大小的二维图形。同理，当四维空间里的物体穿过我们的三维空间时，我们也只能看到一个不停变幻形态和体积的三维物体。")]),v._v(" "),p("p",[v._v("就像扭动万花筒的筒轴，就可以从中看到不停变化的图案？我问王睿一。")]),v._v(" "),p("p",[v._v("是的，只不过万花筒里的图案是二维的。王睿一答道。")]),v._v(" "),p("p",[v._v("刘作爱问王睿一，不管万花筒怎么变，李淳还是吃了屎，你想说明什么？")]),v._v(" "),p("p",[v._v("王睿一不紧不慢地回答，如果二维世界里也有石螺，那它们的屎会被禁锢在一个二维的封闭圈里（也就是自己的身体），比如一个圆圈。但当三维生命想吃掉这个二维石螺时，可以利用“高”这个维度，先把圆圈里的屎给拿出来，且并不破坏圆圈本身（也不通过肛门）。如果把这种行为应用于三维世界，就相当于我们不用开刀就能隔着身体取出内脏一样不可思议。所以在二维生命眼里，三维生命都是魔术师。同理，四维生命也有这样的能力，他们能隔着我们的身体取出屎来，而保持身体的完整。")]),v._v(" "),p("p",[v._v("说到这里，王睿一突然话锋一转，指着我说，李淳就是一个四维生命体！他拥有取出石螺屎的魔力，他把石螺的屎从第四个维度取了出来，但你们这些三维生物看不见那个维度，所以你们认为他把屎吃了下去。")]),v._v(" "),p("p",[v._v("你们这些三维世界来的土逼，我补充说明。")]),v._v(" "),p("p",[v._v("刘作爱一时有些无言，她嘟囔了半天，问王睿一怎么能证明我是四维世界来的？")]),v._v(" "),p("p",[v._v("王睿一当场捧起我的小脸，粗暴地揉搓着，他边揉边给刘作爱分析，说你看他这不断变化的脸，还记得我之前讲解的理论吗？李淳明显就是一只四维生命，正在穿过我们的宇宙。")]),v._v(" "),p("p",[v._v("刘作爱不服，她说她也可以，于是她也旁若无人地拼命揉起自己的脸颊。我和王睿一面面相觑，现场死一般的沉寂。我听见不远处的服务员在窃窃私语：“那靓妹在干嘛？”，“可能在表演兔斯基。”")]),v._v(" "),p("p",[v._v("刘作爱揉完后单方面宣告了自己的胜利，可王睿一不这么认为，他使出了杀手锏，他指着刘作艾的胸问道：你的胸有多大？")]),v._v(" "),p("p",[v._v("刘作爱说，C。")]),v._v(" "),p("p",[v._v("王睿一又问，那可以变成D吗？刘作爱说不能。")]),v._v(" "),p("p",[v._v("王睿一指着我的下体说，他的那个在每天早上就会任意变大，这就是他来自四维宇宙的铁证。")]),v._v(" "),p("p",[v._v("刘作爱长考了很久，终于卑服了，她承认了我来自四维世界，可以隔空取屎。")]),v._v(" "),p("p",[v._v("我满意地对王睿一竖起大拇指，夸他真是辩才无碍，深得魏晋遗风。")]),v._v(" "),p("p",[v._v("第二天，我们去了海边，住在十里银滩酒店恢宏的海景套房里，100多平米的房间，270度的海景阳台，这更激发了王睿一的名士风流。他一大早就扶老携幼地带着几瓶红酒和咖啡，来到阳台上和大海交心。")]),v._v(" "),p("p",[v._v("我揉着朦胧的睡眼，发现王睿一竟然正在对着海水自慰。我勃然大怒地冲过去制止他，问他怎么能做出如此煞风景的举动，你对得起海那边的香港人民吗？")]),v._v(" "),p("p",[v._v("他说你这就不懂了，东晋时的竹林七贤，经常衣不蔽体，放诞落拓。他们号称天地就是自己的衣裤，所以不是他们赤身裸体，而是群众钻进了他们的裤裆。他说竹林七贤长年不洗内裤，导致大家聚会聊天的时候，内内里面阴虱横行，痒不可支，他们不得不边挠边聊，还自得其乐地称之为“扪虱而谈。”")]),v._v(" "),p("p",[v._v("我问他这和你自慰有什么关系？他说他神交古人，这叫“揉蛋而论”。")]),v._v(" "),p("p",[v._v("我悠然神往，心想自己没王睿一有文化就是败在这些细节上，于是我也忍不住把右手放进了裤裆。就在这时楼下的沙滩上突然放起了礼炮，吓得我差点把蛋揉进了盆腔里。")]),v._v(" "),p("p",[v._v("我怒不可遏地对着楼下吼道：什么素质！")]),v._v(" "),p("p",[v._v("楼下传来了一个声音：当新娘的感觉怎么样啊？")]),v._v(" "),p("p",[v._v("我看了看身边的王睿一，他的手仍然放在裤裆里。我心想，我虽然长得秀气点，但凭啥我就是新娘子？我屈辱地想起了威廉王子和凯特王妃大婚后，站在白金汉宫的阳台上对着群众挥手示意的情景，我和王睿一除了手没有挥起来以外，和那个场景如出一辙。")]),v._v(" "),p("p",[v._v("我正在暗自不平，王睿一指指楼下，说人家在举办海滩婚礼呢。")]),v._v(" "),p("p",[v._v("我定睛一看，果然如此，我错怪群众了。沙滩上一个司仪正在采访新娘子，新娘子穿着长长的婚纱，虽然看不清眉眼，但是我能感受到她脸上满满的幸福。")]),v._v(" "),p("p",[v._v("这时新郎以一个颇有魏晋之风的方式登场了，他骑着一匹马，那马很瘦小，以至于更像一头驴，他骑着驴慢慢绕过了一个玫瑰花堆砌成的平台，和新娘相遇。司仪狂喊着：这叫转角遇到爱。")]),v._v(" "),p("p",[v._v("王睿一说，明明就是转角遇到驴。")]),v._v(" "),p("p",[v._v("当天晚上，我们去到楼下的露天酒吧喝酒，参加婚礼的男男女女也熙熙攘攘地汇聚在这里，把酒言欢，以及约炮。王睿一深吸了一口海风，他说空气很咸湿，他嗅到了一股深意，他仿佛感受到了冥冥之中那股让他前来深圳的力量。")]),v._v(" "),p("p",[v._v("我说这种时候陌陌就该派上大用途了，可惜我手机里没有安装。王睿一奸笑着掏出他的三星盖世，告诉我微信也可以查找附近用户的，他满意地在手机里筛选着方圆十里内的女性用户，脸上的笑容陡然僵住了。")]),v._v(" "),p("p",[v._v("他看见了一个熟悉的头像，就在100米之内。")]),v._v(" "),p("p",[v._v("他站了起来，举目四顾，然后目光定格在了换成红色旗袍后的新娘身上。他显然是遇见了故人。")]),v._v(" "),p("p",[v._v("我问王睿一你认识新娘吗？他说他要是不认识，这个世界上就没有别人认识了。")]),v._v(" "),p("p",[v._v("新娘就是当年地震时的那个女医生，她后来辞职离开了华西医院，不知所踪。")]),v._v(" "),p("p",[v._v("王睿一义无反顾地走了过去，他的背影和当年在帐篷里穿梭时一样的笃定。我想象着他即将从驴上抢下新娘，背负在自己宽大的背脊，然后迈着矮子步，背着她回到梦开始的地方。")]),v._v(" "),p("p",[v._v("转角遇到腚，刘作爱在一旁总结道。")]),v._v(" "),p("p",[v._v("后来刘作爱也不知干啥去了，我独自回到了房间，对着月光独酌。我想着王睿一正在像一个魏晋名士那样风流倜傥，心里一阵五味杂陈，不由自主地想揉蛋而论，却想起下午已经被揉进了盆腔，我天生就不是当魏晋名士的料。")]),v._v(" "),p("p",[v._v("然后我听见了门铃，王睿一竟然回来了，我仔细检查了他的裤裆，似乎并没有搏斗的痕迹。我问他和旧情人过得如何？他说我跟她聊了很久，知道她在汶川地震后离开了华西医院，离开了四川，一开始去了广州的一家小医院，后来又做过医疗器材销售和药代表，终是郁郁不得志。最后奉父母之命嫁给了一位山西来的富二代，他是碧桂园的大业主，在十里银滩酒店旁边买了好几栋别墅，所以婚礼也选在了这里。")]),v._v(" "),p("p",[v._v("“爱转角，遇见了驴。”王睿一咽下一大口红酒，口齿不清地唱道。")]),v._v(" "),p("p",[v._v("我才不关心那女人嫁给了马还是驴，我只关心王睿一和她那个没有。我问王睿一，你俩就仅仅进行了清谈？他说是的。他说人家都是新娘了，而我是一个有原则的人。")]),v._v(" "),p("p",[v._v("我打死不信，我趁王睿一喝得酩酊大醉，翻出他的手机偷看微信记录，看见如下一段对话：")]),v._v(" "),p("p",[v._v("女：很开心和你重逢，愿我们来日方长。掟煲唔掟盖，得闲打返场友谊赛。")]),v._v(" "),p("p",[v._v("王睿一：我日理万机。")]),v._v(" "),p("p",[v._v("我看不懂女方那句广东俚语，于是转发给刘作爱，请她帮我翻译下这对话。")]),v._v(" "),p("p",[v._v("刘作爱回复我：‘掟煲唔掟盖，得闲打返场友谊赛’的意思是，锅坏了需要扔掉，但锅盖还可以继续使用，藕断丝连。")]),v._v(" "),p("p",[v._v("我猜出了其中的涵义，这是旧情人之间的约炮用语：等空闲的时候，可以回来打场“友谊赛”。")]),v._v(" "),p("p",[v._v("我倒回去看了看王睿一的回应，由衷地感叹道，这就是魏晋风度，是真名士自风流。我卑服了。")])])}),[],!1,null,null,null);_.default=e.exports}}]);