(window.webpackJsonp=window.webpackJsonp||[]).push([[57],{453:function(v,_,p){"use strict";p.r(_);var t=p(25),n=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("p",[v._v("老师\n公开 2013-05-05 21:11 | (分类:默认分类)")]),v._v(" "),p("p",[v._v("《睿一八部》之七")]),v._v(" "),p("p",[v._v("我的朋友王睿一曾经因为应酬的缘故，频繁出入成都各大夜总会，我通常会在第二天对他进行人文关怀，问他：你玩得开心吗？他总是打着酒嗝回答：我昨晚给小芳/小丽/小月/小芬狠狠上了一课。")]),v._v(" "),p("p",[v._v("大家不要误会，这里的“上课”并没有引申义，而是名副其实的上课。我之所以敢这么保证，是基于我对王睿一的了解，他是一个好为人师的男人。举个例子，曾经有人给王睿一介绍对象，该对象身长1米7，腿长1米5，王睿一和她从酒吧聊到了烧烤摊，然后在该女子的提议下，意犹未尽地去汉庭酒店开房，在标间里俩人畅聊A股形势直到天明。-----据王睿一事后回忆，在前台登记的时候，该女子手机的wifi就“啪”的一声连上了。")]),v._v(" "),p("p",[v._v("“看来她经常去汉庭酒店和人聊A股。”王睿一对她的好学上进赞不绝口。")]),v._v(" "),p("p",[v._v("“只有她才清楚A股凌晨四点的样子。”我补充道。")]),v._v(" "),p("p",[v._v("王睿一就是这样一个执拗到近乎迂腐的男人，他就算在夜总会也不改文人本色，这使得他在那个歌舞升平的场合里显得卓尔不群。比如有的坐台小姐长相清纯，王睿一就凑上去竖起大拇指说：明骚不算骚，闷骚起大包；有的小姐骨瘦如柴，他就安慰人家：先胖不算胖，后胖压坏炕；有一次他的客户钦点了一个长得像姚晨的小妹，他就不住地夸赞哥们有眼光，男人嘴大吃四方，女人嘴大吃裤裆。久而久之，夜总会从经理到小姐都叫他王老师，或者夜总会留几手，他对这些绰号都欣然接受，他说，总比叫我大屁股好。")]),v._v(" "),p("p",[v._v("有一次他在川大附近的一家商务KTV里认识了一个西藏来的汉族女孩，名叫小欣。这姑娘说得一口纯正的普通话，这在充满地市州口音的夜总会里很是招人稀罕，这更勾起了王睿一狂热的授课欲。他直接扑上去握住小欣的手说：你有什么问题就问吧。")]),v._v(" "),p("p",[v._v("小欣问他，尝试过足交吗？王睿一厌恶地摆摆手，说当然没有，他说他这辈子第一次看足交片，还以为是失去双臂的残疾妇女在街头用脚写粉笔字，看得他冲着显示器屏幕扔了好几次零钱。")]),v._v(" "),p("p",[v._v("小欣又问他，一晚上最多有多少次？王睿一说两次，因为第三次还没来得及开始，天就亮了。")]),v._v(" "),p("p",[v._v("小欣又问他最难忘的一次是在哪里，王睿一说不告诉你，小欣说那你把裤子脱了围着包间走一圈。王睿一这才反应过来，高呼我这不是在和你玩真心话大冒险，你能不能问点学术点的问题？历史、经济、民生、数学，都可以！")]),v._v(" "),p("p",[v._v("小欣说那你教我几句四川话吧。王睿一受宠若惊，他作为一个山东移民后裔，四川话一直饱受讥笑，今天居然有人请教他四川话，他当场就摆出了老师的架子。他说，我就教你一个最实用的百搭词汇，球大爷。")]),v._v(" "),p("p",[v._v("小欣问球大爷是什么意思，王睿一说就是表达强烈反对的意思。比如有人向你求婚，而你一点都不愿意，你就可以回答他：“球大爷和你结婚。”在这里，球大爷大概就是就是“你大爷”的意思。又好比，如果你的客人让你一口气喝下10杯酒，你可以回答他：“球大爷喝得了。” 在这里球大爷就是“没有人、不可能”的意思。再好比，你老板让你去陪一个长得像蛤蟆的男人，你不乐意去，就可以说：“球大爷去。”在这里球大爷就代表“大爷不。”")]),v._v(" "),p("p",[v._v("小欣当时有点乱，她闹不明白“球大爷”到底是有大爷还是没大爷，王睿一高深莫测地摸摸自己的胡茬，说我们四川话就是这么博大精深、只可意会，你在生产实践中多运用几次，就明白了。")]),v._v(" "),p("p",[v._v("小欣一脸崇拜地看着王睿一，说你真是个有学问的球大爷。王睿一耐心地纠正她说，不是这么用的，球大爷才这么用。小欣说我就是用的球大爷啊。王睿一板着脸说，下一个问题。")]),v._v(" "),p("p",[v._v("这下小欣是真心把王睿一当成老师了，她准备和王老师交心。她把王睿一拉到包间的一角，说她最近有这么一个烦心事，她上个月接了一个客人，点了她出台，完事后几周了，突然发短信来告诉她，自己用试纸自测出了HIV阳性。她说那个客人强迫她不用套，所以这下等于是和一个艾滋病人无保护性交了，这可如何是好，自己是不是死定了？")]),v._v(" "),p("p",[v._v("王睿一抿了一口酒，神态活像一个大儒，他说你可算找对人了，我是这一带的艾滋病专家。他问小欣，你学过概率学吗？小欣说没有，我只上到初中毕业就出来打工了。王睿一说：我来给你算算你死定了的几率。通常说来，HIV试纸的准确率是99%，即100个阳性结果中会出现1个假阳性，你理解这句话的意思吗？")]),v._v(" "),p("p",[v._v("小欣说我理解，就是说那个男人99%得了艾滋病嘛。")]),v._v(" "),p("p",[v._v("王睿一说你错了，这就是没有受过概率训练的常见谬误。根据统计，HIV在中国大陆的携带率是0.05%，也就是说10000个人里有5个HIV携带者。健康人群的比例为99.95%，根据试纸99%的准确率（即1%的错误率），健康人群测出假阳的比率为99.95%*1%=0.9995%；而HIV携带者的比例0.05%，根据试纸99%的准确率，携带者里测出阳性的比率为0.05%*99%=0.0495%。所以，测出阳性的数量占总人数的比率一共为0.9995%+0.0495%=1.049%.那么，一个自测为阳性的患者，其最终结果为真阳的可能性是0.0495%/1.049%=4.7%. 换句话说，你的这位客人，虽然用试纸自测出了阳性的结果，但他真实患病的几率只有不到5%.")]),v._v(" "),p("p",[v._v("王睿一继续分析，说女人和一个确定的HIV携带者，一次无保护性交的中招几率为千分之一左右。所以说你感染HIV的几率就是4.7%*1/1000=0.0047%, 不到十万分之五。也就是说，你和他发生两万次关系，才会有一次的可能感染HIV。")]),v._v(" "),p("p",[v._v("小欣说她没听懂，但觉得很靠谱的样子。她说王老师学贯中西，你一定是正确的。她问王睿一，如果现在客人问我有没有HIV，我是不是可以正大光明地告诉他，球大爷才有HIV！")]),v._v(" "),p("p",[v._v("是的。王睿一答道，你今晚同时学会了条件概率，和四川话。")]),v._v(" "),p("p",[v._v("小欣激动地掏出钱包，说今晚该我给你钱，这被王睿一谦虚地拒绝了，他说，球大爷收你钱。小欣说那等会儿我跟你走吧，王睿一板着脸吓唬她，说再这样以后你别叫我老师啦！")]),v._v(" "),p("p",[v._v("事后，王睿一讲起这晚上的经过时，我一口咬定王睿一还是怕人家有HIV, 王睿一说他不怕，他相信数学。我问那你为什么拒绝人家？王睿一正色道：哪有老师操学生的！")]),v._v(" "),p("p",[v._v("我算是明白了，这世上所有的人都是王睿一的学生，不论贵贱，不分老幼，他只想教育别人，从来不想操别人。")]),v._v(" "),p("p",[v._v("我决定向王睿一学习，我也要当李老师，走到哪群众都尊重我，去干啥人家都倒给我钱。")]),v._v(" "),p("p",[v._v("我在成都玉林的某健身房办了张年卡，长年在里面健身、洗澡、蒸桑拿。我并不是一个三温暖爱好者，只是我认为健身后蒸桑拿有助于肌肉的放松和恢复。在那里我邂逅了一个大叔，40岁左右的年纪，笑容暧昧不清，肌体平滑细腻，既没有赘肉，也没有肌肉，看起来就像一个老gay. 但他是不是老Gay不重要，因为他竟然不顾年龄和身份的差距，向我拜师学艺。他说他平时只做瑜伽，不从事力量训练，希望以后和我一起锻炼，我可以对他指点一二。我开玩笑说，私教一小时100多元呢，我又不收你钱，你给我什么？他说，嘿嘿。")]),v._v(" "),p("p",[v._v("他笑得我在桑拿房里差点缩回去，要知道在这种温度里缩回去并不符合生理规律，可见我当时确实被他吓着了。但我既然已经发了誓要向王睿一同志学习，就绝不能食言，韩愈说过：无贵无贱，无长无少，道之所存，师之所存也。所以我为了师道，把手机号留给了那个大叔，之后每次健身我都和他约好时间，一起练力量，做器械。他尤其热爱深蹲，并且每次深蹲时都要求我在他身后帮他托着杠铃。我说我站你前面吧，他说不嘛，站前面妨碍我照镜子。于是他每次深蹲，我都以一个老汉推车的造型在他后面站着，双手扶着他的肩，身体还得随着杠铃的上下高低起伏。我本来在健身房和好几个女健友眉来眼去的，从那以后她们都不理\n我好不委屈。")]),v._v(" "),p("p",[v._v("每当这时，我就想想王睿一为了给人讲A股，半夜去开房的故事，心里也就慢慢平衡了。老师都是柳下惠，我激励自己。就这样，我和大叔建立起了师生情。时间如白驹过隙，转眼到了2010年9月，他的深蹲成绩从40公斤上升到了80公斤，屁股也越来越翘，于是他有些膨胀了。当然我事后分析，他当时也有可能是认为教师节将至，要对我表示一下。")]),v._v(" "),p("p",[v._v("那天做完深蹲，我和他又来到了桑拿房，我慵懒地坐在长凳上，心里想着那几个渐行渐远的女健友，百感交集。大叔见我兴致不高，以为我累了，说李老师，你最近身体怎么越来越虚了。我说没办法，我也不年轻了。大叔看了看我身体，说你最近忙着悔我不倦，自己的锻炼都落下了，你看你的小腹，赘肉渐生啊。是我欠你的，是不是该给你交点学费了。")]),v._v(" "),p("p",[v._v("我闭着眼睛，意志模糊地嘟囔，我不要学费，我要女健友。")]),v._v(" "),p("p",[v._v("大叔没听清，他说让他摸摸我的小腹，看看是不是缺乏锻炼。见我不置可否，他便把他粗粝的手掌放在了我的上腹，慢慢向下游走，嘴里自言自语道：2个、4个、6个。。。。我想他是在寻找腹肌，就没搭理他。我突然听见他说，咦，这里怎么还有一个？")]),v._v(" "),p("p",[v._v("我正想问，还有一个什么啊？我突然感到，他竟然一把握住了我的那个。")]),v._v(" "),p("p",[v._v("后来有无数的好事群众问我，那一刻是什么感觉？我说感觉就像在穿越黑洞的表面，或者是突破光速的瞬间，时间停滞了，物理定律消失了。")]),v._v(" "),p("p",[v._v("“时间不存在了。”群众们喃喃地重复着我的话，他们这些碌碌无为一生的凡人，是断然无法理解那种感受的。")]),v._v(" "),p("p",[v._v("总之当我重新感觉到时间存在的时候，我以为共产主义都实现了。我感觉等我推开健身房大门，会看见外面站着各色人种，他们不分种族，相亲相爱，各尽所能，按需分配。但等我清醒过来时，我只看见了一个大叔，他按需分配到了一根JJ，还是我的。")]),v._v(" "),p("p",[v._v("我推开他的魔爪，咒骂着各种脏话，落荒而逃。我逃到了淋浴室里，把水温调到最低，试图说服自己接受刚才发生的一切。但是我冷静不下来，我越来越气愤，觉得自己被王睿一骗了，他不是说老师不会和学生发生那种关系吗？我穿上衣服狂奔回家，找到了王睿一当面对质。")]),v._v(" "),p("p",[v._v("王睿一冷静地告诉我：我当时说的是“老师不能操学生”，我没说学生不能操老师。")]),v._v(" "),p("p",[v._v("我久久不能言语。")]),v._v(" "),p("p",[v._v("王睿一笑了笑说：“你入行尚浅，这点挫折算什么，当老师就要做好被学生辜负的准备。比如我，那个和我去汉庭的女孩儿，她根本就不是去学A股的，要不然怎么一进酒店，手机wifi就啪的一声连上了！她其实就是想搞我，可我还以为她爱学习，和她搞了一晚上A股。这下可好，她那次以后就到处宣传我有生理缺陷。还有，那个夜总会跟我学习四川话的姑娘，她压根就是四川自贡人，只是怕我嘲笑她的口音，才骗我说是西藏人，全程和我说普通话。我他妈一个青岛人，教了一个自贡人一晚上的四川话，嫩妈了个逼的。以后谁再让我教四川话，我就告诉她一句话。”")]),v._v(" "),p("p",[v._v("“哪句话？” 我问。")]),v._v(" "),p("p",[v._v("“球大爷才是四川人。”王睿一用青岛腔哭骂道。")])])}),[],!1,null,null,null);_.default=n.exports}}]);